// src/components/event-form.tsx
import React, { FunctionComponent } from 'react';
import Event from '../models/event';
import formatType from '../helpers/format-type';

type Props = {
    event: Event;
};

const EventForm: FunctionComponent<Props> = ({ event }) => {
    // ... existing code ...

    return (
        <form>
            <div className="row">
                <div className="col s12 m8 offset-m2">
                    <div className="card hoverable">
                        {/* Existing form fields... */}

                        {/* New field for Image URL */}
                        <div className="form-group">
                            <label htmlFor="pictureUrl">Image URL</label>
                            <input
                                id="pictureUrl"
                                type="text"
                                className="form-control"
                                defaultValue={event.pictureUrl} // Assuming 'pictureUrl' is added to the Event model
                            />
                        </div>

                        {/* Rest of the form... */}
                    </div>
                </div>
            </div>
        </form>
    );
};

export default EventForm;
