// src/components/event-form.tsx
import React, { FunctionComponent } from 'react';
import Event from '../models/event';

type Props = {
    event: Event;
};

const EventForm: FunctionComponent<Props> = ({ event }) => {
    // Ajoutez vos chemins d'images ici
    const wineBottle1 = '/path/to/wineBottle1.jpg';
    const wineBottle2 = '/path/to/wineBottle2.jpg';
    const wineBottle3 = '/path/to/wineBottle3.jpg';

    return (
        <form>
            <div className="row">
                {/* ... autres champs du formulaire ... */}

                {/* Sélection de l'image */}
                <div className="form-group">
                    <label>Image de l'événement</label>
                    <div>
                        <label>
                            <input type="radio" name="image" value={wineBottle1} defaultChecked={event.picture === wineBottle1}/>
                            <img src={wineBottle1} alt="Wine Bottle 1" style={{width: '100px'}}/>
                        </label>
                        <label>
                            <input type="radio" name="image" value={wineBottle2} defaultChecked={event.picture === wineBottle2}/>
                            <img src={wineBottle2} alt="Wine Bottle 2" style={{width: '100px'}}/>
                        </label>
                        <label>
                            <input type="radio" name="image" value={wineBottle3} defaultChecked={event.picture === wineBottle3}/>
                            <img src={wineBottle3} alt="Wine Bottle 3" style={{width: '100px'}}/>
                        </label>
                    </div>
                </div>
            </div>
        </form>
    );
};

export default EventForm;
